/*thegagne node:true */
'use strict';

exports.load = function(ffmpeg) {
  ffmpeg
    .format('mov')
    .videoBitrate('512k')
    .videoCodec('libx264')
    .size('720x480')
    .outputOptions('-movflags frag_keyframe+empty_moov')
    .audioBitrate('128k')
    .audioCodec('aac')
    .audioChannels(1);
};